productionStyle.style:
position: relative;
width: 100%;
height: 85vh;
min-height: 780px;
background-color: rgba(255,255,255,1);
display: flex;
flex-direction: column;
align-items: center;
justify-items: end;

productionBorderStyle.style:
display: flex;
justify-content: space-around;
align-items: center;
width: 90%;
height: 60%;
margin-top: 80px;

position: relative;
display: flex;
flex-direction: column;
align-items: center;
justify-content: flex-end;
width: 48%;
height: 80%;
overflow: hidden;

position: absolute;
top: 50%;
z-index: 0;
transform: translateY(-50%);
width: 100%;

z-index: 1;
font-size: 17px;
font-weight: bold;
color: white;

productionBorderAndButton.buttonStyles:
z-index: 1;
font-size: 16px;
background: linear-gradient(to right, #145484 50%, white 50%);
background-size: 200% 100%;
background-position: right bottom;
color: #114a6e;
padding: 10px 50px;
border-radius: 30px;
font-weight: 800;
margin: 10px 0 18px 0;
transition: .4s;

